mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, description = #{description}, image = #{image}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id};

mapper.select:
select s.*,c.name categoryName from setmeal s join category c on c.id = s.category_id

    name like concat('%',#{name},'%')
    status = #{status}
    categoryId = #{categoryId}

order by create_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
